package main

import (
	"fmt"
	"strings"
)

func main() {
	rows := strings.Split(Puzzle, "\n")

	r1d1 := countTrees(1, 1, rows)
	r3d1 := countTrees(3, 1, rows)
	r5d1 := countTrees(5, 1, rows)
	r7d1 := countTrees(7, 1, rows)
	r1d2 := countTrees(1, 2, rows)

	fmt.Printf("Total number of trees (right 1, down 1) %d\n", r1d1)
	fmt.Printf("Total number of trees (right 3, down 1) %d\n", r3d1)
	fmt.Printf("Total number of trees (right 5, down 1) %d\n", r5d1)
	fmt.Printf("Total number of trees (right 7, down 1) %d\n", r7d1)
	fmt.Printf("Total number of trees (right 1, down 2) %d\n", r1d2)
	fmt.Printf("All numbers multiplied together: %d\n", r1d1*r3d1*r5d1*r7d1*r1d2)
}

func countTrees(right int, down int, rows []string) int {
	trees := 0
	col := right

	for i := down; i < len(rows); i += down {
		row := rows[i]
		if row[col] == '#' {
			trees++
		}

		col += right
		if col >= len(row) {
			col = col % len(row)
		}
	}

	return trees
}

